import React, {useState} from "react";
import { VictoryPie } from "victory";

const data = [
    { x: "Business", y: 500 },
    { x: "Salary", y: 700 },
    { x: "Other", y: 300 }
];

const IncomePieChart = ({sortedTransactions}) => {
    const types = {};

    sortedTransactions.forEach(transaction => {
        if (transaction.valueOfTransaction > 0) {
            if (types[transaction.typeOfTransaction]) {
                types[transaction.typeOfTransaction] += transaction.valueOfTransaction;
            } else {
                types[transaction.typeOfTransaction] = transaction.valueOfTransaction;
            }
        }
    });

    const data = Object.keys(types).map((type, index) => ({
        x: type,
        y: types[type],
        label: `${type} (${((types[type] / sortedTransactions.reduce((acc, cur) => cur.valueOfTransaction > 0 ? acc + cur.valueOfTransaction : acc, 0)) * 100).toFixed(2)}%)`,
        color: ['#FFC300', '#FF5733', '#C70039'][index % 3]
    }));

    return (
        <div>
            <VictoryPie
                data={data}
                colorScale={["#FFC300", "#FF5733", "#C70039"]}
                labelRadius={100}
                style={{ labels: { fontSize: 20, fill: "white" } }}
                animate={{ duration: 1000 }}
            />
        </div>
    );
};

export default IncomePieChart;
