import React, {useEffect, useState} from 'react';
import './MainPage.css';
import axios from 'axios';
import {useNavigate} from 'react-router-dom';
import {Backdrop, CircularProgress} from '@mui/material';

import {Link} from "react-router-dom";
import Menu from "../Menu/Menu";

const MainPage = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [user, setUser] = useState({
        lastname:"",
        firstname:"",
        email:"",
        role:""
    });
    const [users, setUsers] = useState([]);
    const [menuActive, setMenuActive] = useState(false);
    const navigate = useNavigate();

    const MenuItems=[{value:"Выйти", action:handleLogoutClick, icon: "logout"}];
    function handleLogoutClick(){
        navigate('/login');
        localStorage.clear();
    }

    useEffect(() => {
        const fetchData = (async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('Token not found in localStorage');
                return;
            }
            try {
                const {data: response} = await axios.get('http://localhost:5000/auth/user', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setUser({
                    lastname: response.lastname,
                    firstname: response.firstname,
                    email: response.email,
                    role: response.roles[0],
                    balance: response.balance
                });
            } catch (error) {
                console.error(error.message);
            }

            setIsLoading(false);
        });
        fetchData();
    }, []);

    return (
        <div className="Users">
            <header>
                <div className="MainBar">
                    <h1 className="logo" onClick={()=>setMenuActive(!menuActive)}>CashR</h1>
                    <div className="userName">
                        <div className="projectIcon">
                            <span className="material-symbols-outlined">person</span>
                        </div>
                        <h1>{user.lastname} {user.firstname}</h1>
                    </div>
                </div>
            </header>

            <div className="mainContent">
                <div className="infoRow">
                    <div className="infoColumn1">
                        <h1>Текущий баланс:</h1>
                        <h1>{user.balance}р.</h1>
                    </div>
                    <div className="infoColumn2">
                        <h1>Сумма всеобщих операций за период:</h1>
                    </div>
                    <div className="infoColumn3">
                        <h1>Сумма расходов за период:</h1>
                    </div>
                    <div className="infoColumn4">
                        <h1>Сумма доходов за период:</h1>
                    </div>
                </div>
            </div>

            <Menu active={menuActive} setActive={setMenuActive} action={true} header={"Главное меню"} items = {MenuItems}/>
           <Backdrop open={isLoading}>
                <CircularProgress/>
            </Backdrop>

        </div>
    );
};

export default MainPage;
