import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";
const ModalFilter = ({setModalFilterIsOpened, modalFilterIsOpened, sortedTransactions, setSortedTransactions, leftBorder, rightBorder, setLeftBorder, setRightBorder}) => {


    function filterByValue(transactions, leftBorder) {
        return transactions.filter((transaction) => {
            const { valueOfTransactions } = transaction.valueOfTransaction;
            return valueOfTransactions >= leftBorder;
        });
    }

    useEffect(()=>{
/*
        setSortedTransactions(filterByValue(sortedTransactions, leftBorder))
*/
        console.log(leftBorder);
    }, [leftBorder, rightBorder])

    function leftBorderChanged(){

    }



    return (
        <div className={modalFilterIsOpened ? "modal active" : "modal"} onClick={function () { setModalFilterIsOpened(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>
                <h1>Введите диапазон значений транзакции:</h1>
                <div className="rangeOfSum">
                    <input type="number" placeholder={"Введите левую границу"} onChange={(e) => {setLeftBorder(e.target.value); leftBorderChanged();}} required />
                    <input type="number" placeholder={"Введите правую границу"} onChange={(e) => setRightBorder(e.target.value)}  required />
                </div>
            </div>
        </div>
    );
};
export default ModalFilter;
