import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";
const ModalChange = ({changeButtonClicked, setIsLoading, selectedUserID, changeUser, setSelectedUserEmail, setSelectedUserLastName, setSelectedUserFirstName, setChangeButtonClicked, selectedUserFirstName, selectedUserLastName, selectedUserEmail}) => {

    const [firstName, setFirstName] = useState(selectedUserFirstName);
    const [lastName, setLastName] = useState(selectedUserLastName);
    const [email, setEmail] = useState(selectedUserEmail);

    function handleClick(){
        setChangeButtonClicked(false);
    }

    function handleChangeUserFirstName(event) {
        setFirstName(event.target.value);
    }
    function handleChangeUserLastName(event){
        setLastName(event.target.value);
    }
    function handleChangeUserEmail(event){
        setEmail(event.target.value);
    }
    return (
        <div className={changeButtonClicked ? "modal active" : "modal"} onClick={function () { setChangeButtonClicked(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>
                <h1>Введите новые данные для пользователя</h1>
                <div className="userFirstName">
                    <h1>Фамилия пользователя:</h1>
                    <input type="text" value={selectedUserFirstName} placeholder={"Введите фамилию пользователя"} onChange={handleChangeUserFirstName} required />
                </div>
                <div className="userLastName">
                    <h1>Имя пользователя:</h1>
                    <input type="text" value={selectedUserLastName} placeholder={"Введите имя пользователя"} onChange={handleChangeUserLastName} required />
                </div>
                <div className="userEmail">
                    <h1>Почта пользователя:</h1>
                    <input type="text" value={selectedUserEmail} placeholder={"Введите почту пользователя"} onChange={handleChangeUserEmail} required />
                </div>
                <div className="buttons">
                    <button>Изменить</button>
                    <button onClick={handleClick}>Отмена</button>
                </div>
            </div>
        </div>
    );
};
export default ModalChange;
