import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";
const ModalFilter = ({setModalFilterIsOpened, modalFilterIsOpened, transactions, sortedTransactions, setSortedTransactions, leftBorder, rightBorder, setLeftBorder, setRightBorder}) => {

    const[standartSet, setStandartSet] = useState(sortedTransactions);

    function filterByValue(transactions, leftBorder, rightBorder) {
        return transactions.filter((transaction) => {
            return parseInt(transaction.valueOfTransaction) >= leftBorder && parseInt(transaction.valueOfTransaction)<= rightBorder;
        });
    }

    useEffect(()=>{
        console.log(standartSet);
        setSortedTransactions(filterByValue(transactions, leftBorder, rightBorder))
    }, [leftBorder, rightBorder])



    return (
        <div className={modalFilterIsOpened ? "modal active" : "modal"} onClick={function () { setModalFilterIsOpened(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>
                <h1>Введите диапазон значений транзакции:</h1>
                <div className="rangeOfSum">
                    <input type="number" placeholder={"Введите левую границу"} onChange={(e) => {setLeftBorder(e.target.value)}} required />
                    <input type="number" placeholder={"Введите правую границу"} onChange={(e) => {setRightBorder(e.target.value)}}  required />
                </div>
            </div>
        </div>
    );
};
export default ModalFilter;
