import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";

const ModalAddTrans = ({setAddTransactionIsOpened, addTransactionIsOpened, typesOfIncomes, typesOfOutcomes}) => {

    return (
        <div className={addTransactionIsOpened ? "modal active" : "modal"} onClick={function () { setAddTransactionIsOpened(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>
                <h1>Введите новые данные для пользователя</h1>
                <div className="userFirstName">
                    <h1>Фамилия пользователя:</h1>
                    <input type="text" placeholder={"Введите фамилию пользователя"}  required />
                </div>
                <div className="userLastName">
                    <h1>Имя пользователя:</h1>
                    <input type="text" placeholder={"Введите имя пользователя"}  required />
                </div>
                <div className="userEmail">
                    <h1>Почта пользователя:</h1>
                    <input type="text" placeholder={"Введите почту пользователя"} required />
                </div>
                <div className="buttons">
                    <button>Изменить</button>
                    <button>Отмена</button>
                </div>
            </div>
        </div>
    );
};
export default ModalAddTrans;
