import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";
const ModalChange = ({changeButtonClicked, setIsLoading, selectedUserID, changeUser, setSelectedUserEmail, setSelectedUserLastName, setSelectedUserFirstName, setChangeButtonClicked, selectedUserFirstName, selectedUserLastName, selectedUserEmail}) => {


    useEffect(()=>{
        try {
            console.log(selectedUserID);
            setIsLoading(true);
            fetch('http://localhost:5000/admin/getUser', {
                method: 'POST',
                headers: {'Content-Type': 'application/json',
                    'Accept': 'application/json',
                },
                body: JSON.stringify({selectedUserID}),
            })
                .then(response => response.json())
                .then(async data => {
                    if(data){
                        setSelectedUserFirstName(data.firstname);
                        setSelectedUserLastName(data.lastname);
                        setSelectedUserEmail(data.email);
                    }
                })
                .catch(error => console.error(error));
        }catch (e){

        }
        setIsLoading(false);
    }, [selectedUserID])
    function handleClick(){
        setChangeButtonClicked(false);
    }

    function handleChangeUserFirstName(event) {
        setSelectedUserFirstName(event.target.value);
    }
    function handleChangeUserLastName(event){
        setSelectedUserLastName(event.target.value);
    }
    function handleChangeUserEmail(event){
        setSelectedUserEmail(event.target.value);
    }

    return (
        <div className={changeButtonClicked ? "modal active" : "modal"} onClick={function () { setChangeButtonClicked(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>
                <h1>Введите новые данные для пользователя</h1>
                <div className="userFirstName">
                    <h1>Фамилия пользователя:</h1>
                    <input type="text" value={selectedUserFirstName} placeholder={"Введите фамилию пользователя"} onChange={e => setSelectedUserFirstName(e.target.value)} required />
                </div>
                <div className="userLastName">
                    <h1>Имя пользователя:</h1>
                    <input type="text" value={selectedUserLastName} placeholder={"Введите имя пользователя"} onChange={e => setSelectedUserLastName(e.target.value)} required />
                </div>
                <div className="userEmail">
                    <h1>Почта пользователя:</h1>
                    <input type="text" value={selectedUserEmail} placeholder={"Введите почту пользователя"} onChange={e => setSelectedUserEmail(e.target.value)} required />
                </div>
                <div className="buttons">
                    <button>Изменить</button>
                    <button onClick={handleClick}>Отмена</button>
                </div>
            </div>
        </div>
    );
};
export default ModalChange;
