import React, {useEffect, useState} from 'react';
import './MainPage.css';
import axios from 'axios';
import {useNavigate} from 'react-router-dom';
import {Backdrop, CircularProgress} from '@mui/material';

import {Link} from "react-router-dom";
import Menu from "../Menu/Menu";
import ModalAddTrans from "../Modal/ModalAddTrans";
import MyDatePickerRange from "../DatePicker/DatePickerRange";

const MainPage = () => {
    const [isLoading, setIsLoading] = useState();
    const [user, setUser] = useState({
        lastname:"",
        firstname:"",
        email:"",
        role:""
    });
    const [users, setUsers] = useState([]);
    const [menuActive, setMenuActive] = useState(false);
    const navigate = useNavigate();

    const[come, setCome] = useState();
    const[addTransactionIsOpened, setAddTransactionIsOpened] = useState(false);
    const[typesOfOutcomes, setTypesOfOutcomes] = useState(["Еда", "Здоровье", "Спорт", "Жилье"]);
    const[typesOfIncomes, setTypesOfIncomes] = useState(["Зарплата", "Стипендия"]);
    const[transactions, setTransactions] = useState([]);

    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(null);

    const MenuItems=[{value:"Выйти", action:handleLogoutClick, icon: "logout"}, {value:"Добавить расход/доход", action:handleAddTransClick, icon:"coin"}];
    function handleLogoutClick(){
        navigate('/login');
        localStorage.clear();
    }
    function handleAddTransClick(){
        setMenuActive(false);
        setAddTransactionIsOpened(true);
    }

    useEffect(() => {
        setIsLoading(true);
        const fetchData = (async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('Token not found in localStorage');
                return;
            }
            try {
                const {data: response} = await axios.get('http://localhost:5000/auth/user', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                await setUser({
                    _id: response._id,
                    lastname: response.lastname,
                    firstname: response.firstname,
                    email: response.email,
                    role: response.roles[0],
                    balance: response.balance
                });
            } catch (error) {
                console.error(error.message);
            }

            await fetch('http://localhost:5000/user/getTransactions', {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify({userID: user._id})
            })
                .then(response => response.json())
                .then(data => {
                    if (data){
                        setTransactions(data);
                    }
                })
                .catch(error => console.error(error));

            setIsLoading(false);
        });
        fetchData();
    }, []);

    return (
        <div className="Users">
            <header>
                <div className="MainBar">
                    <h1 className="logo" onClick={()=>setMenuActive(!menuActive)}>CashR</h1>
                    <div className="userName">
                        <div className="projectIcon">
                            <span className="material-symbols-outlined">person</span>
                        </div>
                        <h1>{user.lastname} {user.firstname}</h1>
                    </div>
                </div>
            </header>
            <div className="mainContent">
                <div className="infoRow">
                    <div className="infoColumn1">
                        <h1>Текущий баланс:</h1>
                        <h1>{user.balance}р.</h1>
                    </div>
                    <div className="infoColumn2">
                        <h1>Сумма всеобщих операций за период:</h1>
                    </div>
                    <div className="infoColumn3">
                        <h1>Сумма расходов за период:</h1>
                    </div>
                    <div className="infoColumn4">
                        <h1>Сумма доходов за период:</h1>
                    </div>
                </div>
                <div>
                    <h1>Транзакции:</h1>
                </div>
                <div>
                    {transactions.length!==0&&transactions.map((transaction) => (
                        <div className="transRow" key={transaction._id}>
                            <div><h2>{transaction.come}</h2></div>
                            <div><h2>{transaction.valueOfTransaction}</h2></div>
                            <div><h2>{transaction.typeOfTransaction}</h2></div>
                            <div><h2>{transaction.dateOfTransaction}</h2></div>
                        </div>
                    ))}
                </div>
            </div>

            <ModalAddTrans come={come} user={user} setIsLoading={setIsLoading} setCome={setCome} typesOfIncomes={typesOfIncomes} typesOfOutcomes={typesOfOutcomes} setAddTransactionIsOpened={setAddTransactionIsOpened} addTransactionIsOpened={addTransactionIsOpened}/>
            <Menu active={menuActive} setActive={setMenuActive} action={true} header={"Главное меню"} items = {MenuItems}/>
           <Backdrop open={isLoading}>
                <CircularProgress/>
            </Backdrop>

        </div>
    );
};

export default MainPage;
