import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";
const ModalChange = ({changeButtonClicked, changeUser, setSelectedUserEmail, setSelectedUserLastName, setSelectedUserFirstName, setChangeButtonClicked}) => {

    function handleClick(){
        setChangeButtonClicked(false);
    }

    function handleChangeUserFirstName(event) {
        setSelectedUserFirstName(event.target.value);
    }

    return (
        <div className={changeButtonClicked ? "modal active" : "modal"} onClick={function () { setChangeButtonClicked(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>
                <h1>Введите новые данные для пользователя</h1>
                <div className="userName">
                    <h1>Имя пользователя:</h1>
                    <input type="text" value={setSelectedUserFirstName} placeholder={"Введите название задачи"} onChange={handleChangeUserFirstName} required />
                </div>
                <div className="buttons">
                    <button>Изменить</button>
                    <button onClick={handleClick}>Отмена</button>
                </div>
            </div>
        </div>
    );
};
export default ModalChange;
