import React, {useEffect, useState} from 'react';
import './Modal.css'
import axios from "axios";
import SingleSelect from "../DropDown/DropDownSingle";

const ModalAddTrans = ({setAddTransactionIsOpened, setCome, come, addTransactionIsOpened, typesOfIncomes, typesOfOutcomes}) => {

    const[typesOfComes, setTypesOfComes] = useState([]);
    const[selectedType, setSelectedType] = useState("");

    useEffect(()=>{
        if(come==="Income"){
            setTypesOfComes(typesOfIncomes);
        }
        else if(come ==="Outcome"){
            setTypesOfComes(typesOfOutcomes);
        }
    }, [setCome])

    return (
        <div className={addTransactionIsOpened ? "modal active" : "modal"} onClick={function () { setAddTransactionIsOpened(false)}}>
            <div className="modal__content" onClick={e => e.stopPropagation()}>

                <h1>Выберите тип транзакции:</h1>
                <div className="come">
                    <div className="Income">
                        <input
                            type="radio"
                            name="come"
                            id="Income"
                            value="Income"
                            defaultChecked={true}
                            onChange={(e) => setCome(e.target.value)}
                        />
                        <label htmlFor="Income">Доход</label>
                    </div>
                    <div className="Outcome">
                        <input
                            type="radio"
                            name="come"
                            id="Outcome"
                            value="Outcome"
                            onChange={(e) => setCome(e.target.value)}
                        />
                        <label htmlFor="Outcome">Расход</label>
                    </div>
                </div>

                <h1>Выберите тип транзакции:</h1>
                <SingleSelect typesOfComes={typesOfComes} setSelectedType={setSelectedType} selectedType={selectedType}/>


                <h1>Введите новые данные для пользователя</h1>
                <div className="userFirstName">
                    <h1>Фамилия пользователя:</h1>
                    <input type="text" placeholder={"Введите фамилию пользователя"}  required />
                </div>
                <div className="userLastName">
                    <h1>Имя пользователя:</h1>
                    <input type="text" placeholder={"Введите имя пользователя"}  required />
                </div>
                <div className="userEmail">
                    <h1>Почта пользователя:</h1>
                    <input type="text" placeholder={"Введите почту пользователя"} required />
                </div>

            </div>
        </div>
    );
};
export default ModalAddTrans;
